VALUES (#{title}, #{content}, #{url}, #{pic}, #{waste_token}, #{must_click}, #{count_click}, #{add_time}, #{businessId}, #{rank}
, #{clickToken})

mapper.update:
UPDATE adv_advert
SET count_click = count_click + 1
WHERE id = #{id}

UPDATE adv_advert
SET status = #{status}
WHERE id = #{id}
